import React, {Component} from 'react';

export default class NewProject extends Component {
	constructor(props) {
		super(props);
		this.state = {
			projectInfo: {
				title: '',
				constructionType: '',
				streetAddress: '',
				city: '',
				state: '',
				zipcode: null,
				description: '',
				images:[],
				postedBy: {adminID:this.props.user.id, adminUsername:this.props.user.username, date:{}}
			}
		}
		this.onSubmit = this.onSubmit.bind(this);
		this.onChangeFormField = this.onChangeFormField.bind(this);
	}
	onSubmit(e) {
		e.preventDefault();

	}

	onChangeFormField(e) {
		console.log(e.target.name);
		console.log(e.target.value);
	}

	render() {
		if (!this.props.user) {
			return(
				<p>Must be logged in as an Administrator to view this page</p>
			)
		}
		return(
			<div>
				<p>Form for new project</p>
				<form onSubmit={this.onSubmit}>
					<div className="form-group">
						<label>Title:</label>
						<input type="text"
							required
							name="title"
							className="form-control"
							placeholder="Enter title to display"
							onChange={this.onChangeFormField}/>
						<label>Construction Type:</label>
						<select type="text"
							required
							name="constructionType"
							className="form-control"
							onChange={this.onChangeFormField}>
							<option value="residential">Residential</option>
							<option value="commercial">Commercial</option>
						</select>
						<label>Street Address:</label>
						<input type="text"
							required
							className="form-control"
							placeholder="Enter Street Address"
							onChange={this.onChangeFormField}/>
						<label>City:</label>
						<input type="text"
							required
							className="form-control"
							placeholder="Enter city for address"
							onChange={this.onChangeFormField}/>
						<label>State:</label>
						<input type="text"
							required
							className="form-control"
							placeholder="Enter state for address"
							onChange={this.onChangeFormField}/>
						<label>Zipcode:</label>
						<input type="text"
							required
							className="form-control"
							placeholder="Enter zipcode for address"
							onChange={this.onChangeFormField}/>
					</div>
					<div className="form-group">
						<input type="submit" value="Submit" className="btn btn-primary"/>
					</div>
				</form>
				<button onClick={()=>{this.props.handlePageChange("HomeMenu")}}>Back to Home Page</button>
			</div>
		)
	}
}
